import { createStore } from 'vuex'

export default createStore({
  state: {
    user: null,
    socket: null
  },
  mutations: {
    SET_USER(state, user) {
      state.user = user
    },
    SET_SOCKET(state, socket) {
      state.socket = socket
    }
  }
})
